import Link from "next/link";
const TopNavbar = () => (
  <nav className="navbar">
    <ul className="navbar-nav">
      <li className="nav-item">
        <Link href="/software">
        <a className="nav-link">
        <svg 
          aria-hidden="true" 
          focusable="false" 
          data-prefix="fas" 
          data-icon="desktop" 
          className="svg-inline--fa fa-desktop fa-w-18" 
          role="img" 
          xmlns="http://www.w3.org/2000/svg" 
          viewBox="0 0 576 512"
          className="fa-primary">
          <path 
            fill="currentColor" 
            d="M528 0H48C21.5 0 0 21.5 0 48v320c0 26.5 21.5 48 48 48h192l-16 48h-72c-13.3 0-24 10.7-24 24s10.7 24 24 24h272c13.3 0 24-10.7 24-24s-10.7-24-24-24h-72l-16-48h192c26.5 0 48-21.5 48-48V48c0-26.5-21.5-48-48-48zm-16 352H64V64h448v288z"></path></svg>
          <span className="link-text">Софтвер</span>
        </a>
        </Link>
      </li>
    </ul>
    <style jsx>
        {`
        .navbar {
        position: fixed;
        background-color: var(--bg-primary);
        transition: width 200ms ease;
        }

        .navbar-nav {
          list-style: none;
          padding: 0;
          margin: 0;
          display: flex;
          flex-direction: column;
          align-items: center;
          height: 100%;
        }

        .nav-item {
          width: 100%;
        }

        .nav-item:last-child {
          margin-top: auto;
        }

        .nav-link {
          display: flex;
          align-items: center;
          justify-content: flex-end
          height: 5rem;
          color: var(--text-primary);
          text-decoration: none;
          filter: grayscale(100%) opacity(0.7);
          transition: var(--transition-speed);
        }

        .nav-link:hover {
          filter: grayscale(0%) opacity(1);
          background: var(--bg-secondary);
          color: var(--text-secondary);
        }

        .link-text {
          display: none;
          margin-left: 1rem;
        }

        .nav-link svg {
          width: 2rem;
          min-width: 2rem;
          margin: 0 1.5rem;
        }

        .fa-primary {
          color: #ff7eee;
        }

        .fa-primary{
          transition: var(--transition-speed);
        }

        .logo {
          font-weight: bold;
          text-transform: uppercase;
          text-align: center;
          color: var(--text-secondary);
          background: var(--bg-secondary);
          font-size: 1.5rem;
          text-transform: uppercase;
          letter-spacing: 0.3ch;
          width: 100%;
        }

        .logo svg {
          transform: rotate(0deg);
          transition: transform var(--transition-speed);
        }

        .navbar:hover .logo svg {
          transform: rotate(-180deg);
        }
        /* Small screens */
        @media only screen and (max-width: 600px) {
          .navbar {
            bottom: 0;
            width: 100vw;
            height: 5rem;
          }
          .navbar-nav {
            flex-direction: row;
          }

          .nav-link {
            justify-content: center;
          }

          main {
            margin: 0;
          }
        }

        /* Large screens */
        @media only screen and (min-width: 600px) {
          .navbar {
            top: 0;
            width: 100vw;
            height: 5rem;
          }

          .navbar:hover {
            width: 12rem;
          }

          .navbar:hover .link-text {
            display: inline;
          }
        }

        .dark {
          --text-primary: #b6b6b6;
          --text-secondary: #ececec;
          --bg-primary: #23232e;
          --bg-secondary: #141418;
        }

        .light {
          --text-primary: #1f1f1f;
          --text-secondary: #000000;
          --bg-primary: #ffffff;
          --bg-secondary: #e4e4e4;
        }

        .solar {
          --text-primary: #576e75;
          --text-secondary: #35535c;
          --bg-primary: #fdf6e3;
          --bg-secondary: #f5e5b8;
        }

        .theme-icon {
          display: none;
        }

        .dark #darkIcon {
          display: block;
        }

        .light #lightIcon {
          display: block;
        }

        .solar #solarIcon {
          display: block;
  }
      `}
    </style>
  </nav>
);

export default TopNavbar;
