import styled from 'styled-components'

const Button = styled.button`
  body: $
  background: ${props => props.theme.bg};
  color: ${props => props.theme.fontColor};
`

Button.defaultProps = {
  theme: {
    bg: 'white',
    fontColor: 'black'
  }
}

export default Button
