import Link from "next/link";
const Navbar = () => (
  <nav className="navbar">
  <head>
  <meta name="viewport" content="width=device-width, initial-scale=1.0"/>
  </head>
    <ul className="navbar-nav">
      <li className="logo">
      <a href="#" className="nav-link">
        <i class="far fa-newspaper fa-2x"></i>
        <span className="link-text">GNews</span>
        </a>
      </li>
      <li className="nav-item">
        <a href="#" className="nav-link">
        <i class="far fa-newspaper fa-2x"></i>
        <span className="link-text">Мэдээ</span>
        </a>
      </li>
      <li className="nav-item">
        <a href="#" className="nav-link">
        <i class="fas fa-gamepad fa-2x"></i>
        <span className="link-text">Тоглоом</span>
        </a>
      </li>
      <li className="nav-item">
        <a href="#" className="nav-link">
        <i class="far fa-newspaper fa-2x"></i>
        <span className="link-text">Софтвер</span>
        </a>
      </li>
      <li className="nav-item">
        <a href="#" className="nav-link">
        <i class="far fa-newspaper fa-2x"></i>
        <span className="link-text">Aliens</span>
        </a>
      </li>
    </ul>
    <style jsx>
      {`
      .navbar{
        position:fixed;
        background-color: #2A2E35;
      }
      .navbar-nav{
        list-style: none;
        padding: 0;
        margin: 0;
        display: flex;
        flex-direction: column;
        align-itmes: center;
        height: 100%;
      }
      .nav-item{
        width: 100%;
      }
      .nav-item:last-child{
        margin-top: auto;
      }
      .nav-link{
        align-items: center;
        padding-left: 1.2rem;
        height: 5rem;
        color: var(--text-primary);
        text-decoration: none;
        filter: grayscale(100%) opacity(0.7);
        transition: var(--transition-speed);
      }
      .nav-link:hover{
        filter: grayscale(0%) opacity(1);
        background: var(--bg-secondary);
        color: var(--teext-secondary);
      }
      .link-text{
        display:none;
        margin-left:1rem;
      }
      .nav-link svg{
        min-width:2rem;
        margin: 0 1.5rem;
      }
     
      .fa-primary{
        color: #ff7eee;
      }
      .fa-secondary{
        color: #df49a6;
      }
      .fa-primary,
      .fa-secondary{
        transition: var(--transition-speed);
      }
      .logo{
        font-weight:bold;
        test-transform: uppercase;
        font-size: 1.2rem;
        text-transform: uppercase;
        letter-spaceng: 0.3ch;
      }


@media only screen and (min-width: 600px) {
  .navbar {
    top: 0;
    width: 5rem;
    height: 100vh;
  }
}
/* Small screens */
@media only screen and (max-width: 600px) {
  .navbar {
    bottom: 0;
    width: 100vw;
    height: 5rem;
  }

  .logo {
    display: none;
  }

  .navbar-nav {
    flex-direction: row;
  }

  .nav-link {
    justify-content: center;
  }

  .container{
    margin: 0;
  }
}

  .dark {
    --text-primary: #b6b6b6;
    --text-secondary: #ececec;
    --bg-primary: #23232e;
    --bg-secondary: #141418;
  }

  .light {
    --text-primary: #1f1f1f;
    --text-secondary: #000000;
    --bg-primary: #ffffff;
    --bg-secondary: #e4e4e4;
  }

  .solar {
    --text-primary: #576e75;
    --text-secondary: #35535c;
    --bg-primary: #fdf6e3;
    --bg-secondary: #f5e5b8;
  }

  .theme-icon {
    display: none;
  }

  .dark #darkIcon {
    display: block;
  }

  .light #lightIcon {
    display: block;
  }

  .solar #solarIcon {
    display: block;
  }
      `}
    </style>
  </nav>
);

export default Navbar;
