import Link from "next/link";
const Navbar = () => (
  <nav className="navbar">
    <head>
      <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    </head>
    <ul className="navbar-nav">
      <li className="logo">
        <a href="#" className="nav-link">
          <span className="link-text">Fireship</span>
          <svg 
            aria-hidden="true" 
            focusable="false" 
            data-prefix="fas" 
            data-icon="dragon" 
            class="svg-inline--fa fa-dragon fa-w-20" 
            role="img" xmlns="http://www.w3.org/2000/svg" 
            viewBox="0 0 640 512">
              <path 
                fill="currentColor" 
                d="M18.32 255.78L192 223.96l-91.28 68.69c-10.08 10.08-2.94 27.31 11.31 27.31h222.7c-9.44-26.4-14.73-54.47-14.73-83.38v-42.27l-119.73-87.6c-23.82-15.88-55.29-14.01-77.06 4.59L5.81 227.64c-12.38 10.33-3.45 30.42 12.51 28.14zm556.87 34.1l-100.66-50.31A47.992 47.992 0 0 1 448 196.65v-36.69h64l28.09 22.63c6 6 14.14 9.37 22.63 9.37h30.97a32 32 0 0 0 28.62-17.69l14.31-28.62a32.005 32.005 0 0 0-3.02-33.51l-74.53-99.38C553.02 4.7 543.54 0 533.47 0H296.02c-7.13 0-10.7 8.57-5.66 13.61L352 63.96 292.42 88.8c-5.9 2.95-5.9 11.36 0 14.31L352 127.96v108.62c0 72.08 36.03 139.39 96 179.38-195.59 6.81-344.56 41.01-434.1 60.91C5.78 478.67 0 485.88 0 494.2 0 504 7.95 512 17.76 512h499.08c63.29.01 119.61-47.56 122.99-110.76 2.52-47.28-22.73-90.4-64.64-111.36zM489.18 66.25l45.65 11.41c-2.75 10.91-12.47 18.89-24.13 18.26-12.96-.71-25.85-12.53-21.52-29.67z"
                className="fa-secondary"
              ></path>
            </svg>
        </a>
      </li>
      <li className="nav-item">
        <a href="#" className="nav-link">
          <svg 
            aria-hidden="true" 
            focusable="false" 
            data-prefix="far" 
            data-icon="newspaper" 
            class="svg-inline--fa fa-newspaper fa-w-18" 
            role="img" xmlns="http://www.w3.org/2000/svg" 
            viewBox="0 0 576 512"
            className="fa-primary"
            ><path fill="currentColor" d="M552 64H112c-20.858 0-38.643 13.377-45.248 32H24c-13.255 0-24 10.745-24 24v272c0 30.928 25.072 56 56 56h496c13.255 0 24-10.745 24-24V88c0-13.255-10.745-24-24-24zM48 392V144h16v248c0 4.411-3.589 8-8 8s-8-3.589-8-8zm480 8H111.422c.374-2.614.578-5.283.578-8V112h416v288zM172 280h136c6.627 0 12-5.373 12-12v-96c0-6.627-5.373-12-12-12H172c-6.627 0-12 5.373-12 12v96c0 6.627 5.373 12 12 12zm28-80h80v40h-80v-40zm-40 140v-24c0-6.627 5.373-12 12-12h136c6.627 0 12 5.373 12 12v24c0 6.627-5.373 12-12 12H172c-6.627 0-12-5.373-12-12zm192 0v-24c0-6.627 5.373-12 12-12h104c6.627 0 12 5.373 12 12v24c0 6.627-5.373 12-12 12H364c-6.627 0-12-5.373-12-12zm0-144v-24c0-6.627 5.373-12 12-12h104c6.627 0 12 5.373 12 12v24c0 6.627-5.373 12-12 12H364c-6.627 0-12-5.373-12-12zm0 72v-24c0-6.627 5.373-12 12-12h104c6.627 0 12 5.373 12 12v24c0 6.627-5.373 12-12 12H364c-6.627 0-12-5.373-12-12z"></path></svg>
          <span className="link-text">Cats</span>
        </a>
      </li>
      <li className="nav-item">
        <a href="#" className="nav-link">
          <span className="link-text">Cats</span>
        </a>
      </li>
      
    </ul>
    <style jsx>
      {`
      .navbar {
      position: fixed;
      background-color: var(--bg-primary);
      transition: width 200ms ease;
    }

    .navbar-nav {
      list-style: none;
      padding: 0;
      margin: 0;
      display: flex;
      flex-direction: column;
      align-items: center;
      height: 100%;
    }

    .nav-item {
      width: 100%;
    }

    .nav-item:last-child {
      margin-top: auto;
    }

    .nav-link {
      display: flex;
      align-items: center;
      height: 5rem;
      color: var(--text-primary);
      text-decoration: none;
      filter: grayscale(100%) opacity(0.7);
      transition: var(--transition-speed);
    }

    .nav-link:hover {
      filter: grayscale(0%) opacity(1);
      background: var(--bg-secondary);
      color: var(--text-secondary);
    }

    .link-text {
      display: none;
      margin-left: 1rem;
    }

    .nav-link svg {
      width: 2rem;
      min-width: 2rem;
      margin: 0 1.5rem;
    }

    .fa-primary {
      color: #ff7eee;
    }

    .fa-secondary {
      color: #df49a6;
    }

    .fa-primary,
    .fa-secondary {
      transition: var(--transition-speed);
    }

    .logo {
      font-weight: bold;
      text-transform: uppercase;
      margin-bottom: 1rem;
      text-align: center;
      color: var(--text-secondary);
      background: var(--bg-secondary);
      font-size: 1.5rem;
      text-transform: uppercase;
      letter-spacing: 0.3ch;
      width: 100%;
    }

    .logo svg {
      transform: rotate(0deg);
      transition: transform var(--transition-speed);
    }

    .navbar:hover .logo svg {
      transform: rotate(-180deg);
    }

    /* Small screens */
    @media only screen and (max-width: 600px) {
      .navbar {
        bottom: 0;
        width: 100vw;
        height: 5rem;
      }

      .logo {
        display: none;
      }

      .navbar-nav {
        flex-direction: row;
      }

      .nav-link {
        justify-content: center;
      }

      main {
        margin: 0;
      }
    }

    /* Large screens */
    @media only screen and (min-width: 600px) {
      .navbar {
        top: 0;
        width: 5rem;
        height: 100vh;
      }

      .navbar:hover {
        width: 16rem;
      }

      .navbar:hover .link-text {
        display: inline;
      }
    }

    .dark {
      --text-primary: #b6b6b6;
      --text-secondary: #ececec;
      --bg-primary: #23232e;
      --bg-secondary: #141418;
    }

    .light {
      --text-primary: #1f1f1f;
      --text-secondary: #000000;
      --bg-primary: #ffffff;
      --bg-secondary: #e4e4e4;
    }

    .solar {
      --text-primary: #576e75;
      --text-secondary: #35535c;
      --bg-primary: #fdf6e3;
      --bg-secondary: #f5e5b8;
    }

    .theme-icon {
      display: none;
    }

    .dark #darkIcon {
      display: block;
    }

    .light #lightIcon {
      display: block;
    }

    .solar #solarIcon {
      display: block;
}
      `}
    </style>
  </nav>
);

export default Navbar;
