import Link from "next/link";
import styled from 'styled-components'
const Main = styled.main`
            display: grid;
            grid-template-columns: 1fr repeat(12, minmax(auto, 60px)) 1fr;
            grid-gap: 40px;
            padding: 60px 0;
          *{
            box-sizing: border-box;
            padding: 0;
            margin: 0;
          }
          .text--medium{
            line-height: 20px;
            font-weight: 400;
          }
          .cards{
            grid-column: 2/ span 12;
            display: grid;
            grid-template-columns: repeat(12, minmax(auto, 60px));
            grid-gap: 40px;
          } 
          .card{
            grid-column-end: span4;
            display: flex;
            flex-direction: column;
            background-color: #353637;
            cursor: pointer;
            transition: all 0.3s ease 0s;\
          }
          .card__image-container{
            width: 100%;
            padding-top: 56.25%;
            overflow: hidden;
            posti
          }
          
        `
const Cards = () => (
  <Main>
    <section className="cards">
      <div className="card">
        <div className="card__image-container">
          <img src="https://images.unsplash.com/photo-1464822759023-fed622ff2c3b?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1200&q=80"></img>
        </div>
        <div className="card__content">
          <p className="card__title text--medium">Here's the Title of an Awesome Course</p>
          <div className="card__info">
            <p className="text--medium">30min</p> 
            <p className="card__price text--medium">Free</p>
          </div>
        </div>
      </div>
  </section>
  </Main>
);

export default Cards;
