import Layout from '../components/Layout';
import Container1 from '../components/Container1';
import styled from 'styled-components'
const Div = styled.div`
            	max-width: 88%;
	margin: 0 auto;
            .cards{
                display: -webkit-flex;
	display: flex;
	-webkit-justify-content: center;
	justify-content: center;
	-webkit-flex-wrap: wrap;
	flex-wrap: wrap;
	margin-top: 15px;
	padding: 1.5%;
	-webkit-box-sizing: border-box;
	-moz-box-sizing: border-box;
	box-sizing: border-box;
}
                        .cards__item {
                            @media (max-width: 700px) {
		width: 100%;
	}
	position: relative;
	margin-bottom: 20px; 
	padding-bottom: 30px; 
	background: #fefff9;
	color: #363636; //the whole content of the card becomes linked, so this will give the text a "norma;" appearance
	text-decoration: none;
	@include box-shadow(rgba(black, 0.19) 0 0 8px 0);
	@include border-radius(4px);
	@include breakpoint(700px, $no-query: true) {
	//switch to 2 columns
		max-width: 320px;
		margin-right: 20px;
		margin-bottom: 20px;
		&:nth-child(even) {
			margin-right: 0;
		}
	}
	@include breakpoint(980px, $no-query: true) {
	//switch to 3 columns
		&:nth-child(even) {
			margin-right: 20px;
		}
		&:nth-child(3n) {
			margin-right: 0;
		}
	}
  }
            
            
            `
const About = ()=> (
    <Layout>
        <div>
        <h1>Games to download</h1>
        <Div>
            <ul className="cards">
                <li className="cards__item"><Container1/></li>
                <li className="cards__item"><Container1/></li>
                <li className="cards__item"><Container1/></li>
                 <li className="cards__item"><Container1/></li>
            </ul>
        </Div>
        </div>
    </Layout>
);
export default About;
